import React from 'react';
import logo from './logo.svg';
import './App.scss';
import jQuery from 'jquery';
import 'bootstrap';
import {default as Web3} from 'web3';
// import patient_artifacts from '../../build/contracts/PatientIndex.json'
var Accounts = require('web3-eth-accounts');

window.$ = window.jQuery = jQuery;
// var Patient = contract(patient_artifacts);



// Patient.setProvider(web3.currentProvider);

const web3 = new Web3(Web3.givenProvider || "http://localhost:7545" );
const ethereumButton = document.querySelector(".enableEthereumButton");



window.addEventListener('load', function () {
  if (typeof web3 !== 'undefined') {        
      //window.web3 = new Web3(window.web3.currentProvider)

      var account = null;
      if (web3.currentProvider.isMetaMask === true) {
          web3.eth.getAccounts((err, accounts) => {
              if (accounts.length == 0) {
                  // there is no active accounts in MetaMask
                  this.console.log('there is no active accounts in MetaMask')
                  // web3.eth.getAccounts().then(console.log);
                  this.console.log(accounts[0])
              }
              else {
                  // It's ok
                  web3.eth.getAccounts(console.log);
              }
          });
      } else {
          // Another web3 provider
      }
  } else {
      // No web 3 provider
  }    
});
ethereumButton.addEventListener("click", () => {
  //Will Start the metamask extension
  web.currentProvider.enable();
});
